function previewImage(n){const t=new FileReader;t.onload=function(){const n=document.getElementById("profileImagePreview");n.src=t.result};n.target.files&&n.target.files[0]&&t.readAsDataURL(n.target.files[0])}function filterCities(){const n=document.getElementById("searchBar").value.toLowerCase(),t=document.querySelectorAll(".city-card");t.forEach(t=>{const i=t.querySelector("span").textContent.toLowerCase();t.style.display=i.includes(n)?"block":"none"})}function clearFilter(){document.getElementById("searchBar").value="";filterCities()}